function promiseAll(promises) {
  return new Promise((resolve, reject) => {
    const results = []
    let resolvedCount = 0

    promises.forEach((promise, index) => {
      promise
        .then(result => {
          results[index] = result

          resolvedCount++

          if (resolvedCount === promises.length) {
            resolve(results)
          }
        })
        .catch(err => reject(err))
    })
  })
}

