class CustomPromise {
  constructor(action) {
    this.state = 'PENDING'
    this.value = undefined
    this.thenCallbacks = []
    this.errorCallbacks = []
    action(this.resolver.bind(this), this.reject.bind(this))
  }

  resolver(value) {
    this.state = 'RESOLVED'
    this.value = value
    this.thenCallbacks.forEach(callback => {
      callback(this.value)
    })
  }

  reject(value) {
    this.state = 'REJECTED'
    this.value = value
    this.errorCallbacks.forEach(callback => {
      callback(this.value)
    })
  }

  then(callback) {
    this.thenCallbacks.push(callback)
    return this
  }

  catch(callback) {
    this.errorCallbacks.push(callback)
    return this
  }
}

